/**
 * Format the avatar url to the correct size
 * @param avatarUrl - The url of the avatar
 * @returns The formatted avatar url
 */
export const formatAvatarUrl = (avatarUrl: string) => {
  const defaultAvatar = "/anim=false,fit=contain,f=auto,w=512";
  if (avatarUrl.endsWith("/rectcrop3")) {
    avatarUrl = avatarUrl.replace("/rectcrop3", defaultAvatar);
  } else if (avatarUrl.endsWith("/original")) {
    avatarUrl = avatarUrl.replace("/original", defaultAvatar);
  } else if (avatarUrl.endsWith("/public")) {
    avatarUrl = avatarUrl.replace("/public", defaultAvatar);
  }
  return avatarUrl;
};
