  const handleClose = () => {
    setStep("initial");
    setError("");
    setTxHash("");
  };

  const handleRetry = () => {
    setError("");
    setStep("sheet");
  };

  // Step 1: Initial Mint Button
  if (step === "initial") {
    return (
      <Button
        variant={variant}
        size={size}
        onClick={handleInitialMint}
        disabled={disabled || !isSDKLoaded}
        className={cn("w-full", className)}
